const buttonTop=document.querySelector("#toTop");$(window).on("load",(function(){$(".gallery").liMarquee({circular:!0,startShow:!1,scrollamount:200,scrolldelay:0,loop:-1,drag:!0}),$(".venobox").venobox({spinner:"double-bounce",numeration:!0,numerationColor:"#ffffff"})}));const scrollFunc=()=>{let o=window.scrollY;buttonTop.style.display=o>0?"flex":"none"};window.addEventListener("scroll",scrollFunc);const scrollToTop=()=>{const o=document.documentElement.scrollTop||document.body.scrollTop;o>0&&(window.requestAnimationFrame(scrollToTop),window.scrollTo(0,o-o/10))};buttonTop.onclick=function(o){o.preventDefault(),scrollToTop()};
